"use strict";var canvas=document.querySelector("#main-canvas");canvas.width=canvas.clientWidth,canvas.height=canvas.clientHeight;var ctx=canvas.getContext("2d"),submitButton=document.querySelector("#submitChanges"),resetButton=document.querySelector("#resetChanges");function randomIntFromRange(t,n){return Math.random()*(n-t+1)+t}function getRandomColor(){var t="#";return t+=((1<<24)*Math.random()|0).toString(16)}function Circle(t,n,e,i,r,a){this.x=t,this.y=n,this.dx=e,this.dy=i,this.rad=r,this.color=a,this.draw=function(){ctx.beginPath(),ctx.arc(this.x,this.y,this.rad,0,2*Math.PI),ctx.fillStyle=this.color,ctx.fill()},this.update=function(){this.x+=this.dx,this.y+=this.dy;var t=document.querySelector("#walls").value;"wall"===t?((this.y+this.rad>window.innerHeight||this.y+this.rad<0)&&(this.dy=-this.dy),(this.x+this.rad>window.innerWidth||this.x-this.rad<0)&&(this.dx=-this.dx)):"nowall"===t&&(this.y<0?this.y=window.innerHeight:this.y>window.innerHeight?this.y=0:this.y=this.y,this.x<0?this.x=window.innerWidth:this.x>window.innerWidth?this.x=0:this.x=this.x),this.draw()}}submitButton.addEventListener("click",function(){init()}),resetButton.addEventListener("click",function(){reset()}),window.addEventListener("resize",function(){canvas.width=window.innerWidth,canvas.height=window.innerHeight});var circles=[];function init(){circles=[];for(var t=document.querySelector("#particles-number").value,n=document.querySelector("#radius").value,e=document.querySelector("#color").value,i=document.querySelector("#xspeed").value,r=document.querySelector("#xfactor").value,a=document.querySelector("#yspeed").value,o=document.querySelector("#yfactor").value,s=0;s<t;s++){var d=Math.abs(n)||randomIntFromRange(2,4),c=Math.random()*(window.innerWidth-2*d),h=Math.random()*(window.innerHeight-2*d),u=parseInt(i,10)*randomIntFromRange(0,r)||randomIntFromRange(.1,5),l=parseInt(-a,10)*randomIntFromRange(0,o)||-randomIntFromRange(.1,5),m=e||getRandomColor();circles.push(new Circle(c,h,u,l,d,m))}}function reset(){circles=[];for(var t=0;t<100;t++){var n=randomIntFromRange(2,4),e=Math.random()*(window.innerWidth-2*n),i=Math.random()*(window.innerHeight-2*n),r=randomIntFromRange(.1,5),a=-randomIntFromRange(.1,5),o=getRandomColor();circles.push(new Circle(e,i,r,a,n,o))}}function animation(){requestAnimationFrame(animation),ctx.clearRect(0,0,window.innerWidth,window.innerHeight);for(var t=0;t<circles.length;t++)circles[t].update()}animation(),init();