"use strict";console.log("js connected");var colorInput=document.querySelector("#color"),canvas=document.querySelector("#main-canvas");canvas.width=canvas.clientWidth,canvas.height=canvas.clientHeight;var ctx=canvas.getContext("2d"),mouse={x:void 0,y:void 0},submitButton=document.querySelector("#submitChanges");function randomIntFromRange(t,i){return Math.random()*(i-t+1)+t}function getRandomColor(){var t="#";return t+=((1<<24)*Math.random()|0).toString(16)}function Circle(t,i,n,e,o,a,r,s){this.x=t,this.y=i,this.dx=n,this.dy=e,this.rad=o,this.color=a,this.boundaryRight=r,this.boundaryLeft=s;var c=o,h=2*o;this.draw=function(){ctx.beginPath(),ctx.arc(this.x,this.y,this.rad,0,2*Math.PI),ctx.fillStyle=this.color,ctx.fill()},this.update=function(){this.y+this.rad<0&&(this.y=window.innerHeight),(this.x>r||this.x<s)&&(this.dx=-this.dx),this.x+=this.dx,this.y+=this.dy,mouse.x-this.x<50&&-50<mouse.x-this.x&&mouse.y-this.y<50&&-50<mouse.y-this.y?this.rad<h&&(this.rad+=1):this.rad>c?this.rad-=1:this.rad<this.minRad&&(this.rad+=1),this.draw()}}submitButton.addEventListener("click",function(){canvas.style.color=colorInput.value,init()}),window.addEventListener("mousemove",function(t){mouse.x=t.x,mouse.y=t.y}),window.addEventListener("resize",function(){canvas.width=window.innerWidth,canvas.height=window.innerHeight,init()});var circles=[];function init(){circles=[];for(var t=0;t<100;t++){var i=randomIntFromRange(2,4),n=Math.random()*(window.innerWidth-2*i),e=Math.random()*(window.innerHeight-2*i),o=-randomIntFromRange(.2,.3),a=colorInput.value||getRandomColor(),r=n+i,s=n-i;circles.push(new Circle(n,e,.2,o,i,a,r,s))}}function animation(){requestAnimationFrame(animation),ctx.clearRect(0,0,window.innerWidth,window.innerHeight);for(var t=0;t<circles.length;t++)circles[t].update()}animation(),init();